func:
msg.url = "http://rec.juhlin.me/record/start" ;

msg.payload = {
    serial: flow.get("camera").serial,
    buffer: flow.get("bufferTime") || 5,
    tag: "Detection",
    method: "FMR"
}

return msg;

msg.url = "http://rec.juhlin.me/record/stop" ;

msg.payload = {
    serial: flow.get("camera").serial,
    buffer: 7,
    tag: "Detection",
    method: "FMR"
}

return msg;

if( !flow.get("classes") ) {
    msg.payload = [ "Human", "Car" ];
} else {
    msg.payload = flow.get("classes");
}

return msg;

msg.payload = flow.get("camera") || {
    aspect: "16:9",
    rotation: "Landscape",
    image: ""
}    
flow.set("camera",msg.payload);

return msg;

msg.topic = "aoi";
msg.payload = flow.get("aoi") || {
    id: "aoi",
    x1: 200,
    y1: 200,
    x2: 800,
    y2: 800
}

return msg;

msg.query = {
    device: flow.get("device").serial || "Undefined"
}

msg.payload = {
    device: flow.get("device").serial || "Undefined",
    name: flow.get("name") || flow.get("device").hostname || flow.get("device").name,
    timestamp: new Date().getTime(),
    rotation: msg.payload.rotation,
    aspect: msg.payload.aspect,
    image: msg.payload.image
}

msg.topic = "image/" + flow.get("device").serial || "Undefined";


return msg;

var camera = flow.get("camera");
if(!camera)
    return;

switch( camera.aspect ) {
    case "4:3":
        msg.resolution = "800x600"
        break;
    case "1:1":
        msg.resolution = "800x800"
        break;
    case "16:9":
    default:
        msg.resolution = "1280x720"
}

return msg;

msg.topic = "detections";
var distance = flow.get("distance") || 0;
var confidence = flow.get("confidence") || 10;
var age = flow.get("age") || 0;
var classes = flow.get("classes");
var aoi = flow.get("aoi") || null;
var list = [];

msg.payload.forEach( function(item){
    if (msg.payload.confidnece < confidence)
        return;
    if( item.age < 0)
        return;
    var dx = (item.cx - item.bx);
    dx = dx * dx;
    var dy = (item.cy - item.by);
    dy = dy * dy;
    item.distance = parseInt(Math.sqrt(dx+dy)/10);
    if( item.distance < distance )
        return;

    var ignoreClass = true;
    classes.forEach(function (element) {
        if (element === item.class)
            ignoreClass = false;
    });
    if (ignoreClass)
        return;

    if (aoi) {
        if( item.cx < aoi.x1 )
            return;
        if (item.cx > aoi.x2)
            return;
        if (item.cy < aoi.y1)
            return;
        if (item.cy > aoi.y2)
            return;
    }
    list.push(item);
});

if( list.length === 0 )
    return 0;
msg.payload = list;
node.send(msg);

msg.topic = "image";

msg.payload = {
    aspect: flow.get("aspect") || "16:9",
    rotation: flow.get("rotation") || "Landscape",
    image: msg.payload
}

return msg;

var name = "Undefined";
if( flow.get("camera") && flow.get("camera").hasOwnProperty("serial") )
    name = flow.get("camere").serial;
msg.topic = "exception/Detection_Recording_Control/" + name;
msg.payload = msg.error.message;
return msg;
