function CRC16_Calc(data,bytes,initVal) {
  // init crc
  let crc = initVal;
  // iterate over all bytes
  let i = 0;
  while (bytes--) {
    // calc new crc
    crc = (crc >>> 8) ^ CRC16_Table[(crc ^ data[i++]) & 0x00FF];
  }
  // return result
  return crc;
}

