let i = 0;
let j = 0;
let newmsg = {payload : []};
while (i < payload.length) {
    var c = payload[i++];
    switch (c) {
    // if it’s an END character then we’re done with the packet
    case SLIP_END:
      // a minor optimization: if there is no
      // data in the packet, ignore it. This is
      // meant to avoid bothering IP with all
      // the empty packets generated by the
      // duplicate END characters which are in
      // turn sent to try to detect line noise.
      if(j) j = 0;
      break;
    // if it’s the same code as an ESC character, wait and get another
    // character and then figure out what to store in the packet based on that.
    case SLIP_ESC:
      c = payload[i++];
      // if "c" is not one of these two, then we have a protocol violation.
      // The best bet seems to be to leave the byte alone and just stuff
      // it into the packet
      switch(c) {
      case SLIP_ESC_END:
        c = SLIP_END;
        break;
      case SLIP_ESC_ESC:
        c = SLIP_ESC;
        break;
      }
      // here we fall into the default handler and let it store the character for us
    default:
      newmsg.payload[j++] = c;
    }
}
return newmsg;

