const RX_CHANNEL_INFO_BIT_MASK = 1 << 0;

function identifyEndpoint(payload) {
    let ret = {};
    if (payload.length >= (ENDPOINT_ID_POS + 1)) {
        switch (payload[ENDPOINT_ID_POS]) {
            case LORAWAN_ID:
                {
                    return identifyLoRaWANMessage(payload);
                    break;
                }
        }
    }
    return null;  //ret.info = "no known endpoint id found";
}


function identifyLoRaWANMessage(payload) {
    let ret = {}
    if (payload.length >= (MSG_ID_POS + 1)) {
        switch (payload[MSG_ID_POS]) {
            case LORAWAN_MSG_RECV_UDATA_IND:
                {
                    return analyzeUnreliableDataIndication(payload);
                }
            case LORAWAN_MSG_RECV_CDATA_IND:
                {
                    return analyzeReliableDataIndication(payload);
                }
        }
    }
    ret = null; //ret.info = "no known endpoint id found";
    return ret;
}

function analyzeUnreliableDataIndication(payload) {
    return analyzeDataIndication(payload);
}

function analyzeReliableDataIndication(payload) {
    return analyzeDataIndication(payload);
}

function analyzeDataIndication(payload) {
    let ret = {};
    if (payload.length >= (DATA_POS + 1)) {
        let status = payload[STATUS_POS];
        let port = payload[PORT_POS];
        let length = payload.length;
        if ((status && RX_CHANNEL_INFO_BIT_MASK) == RX_CHANNEL_INFO_BIT_MASK) {
            //substract channel info from data length
            length = -5
            ret.channelIndex = payload.at(-5);
            ret.datareateIndex = payload.at(-4);
            ret.rssiIndex = payload.at(-3);
            ret.snrIndex = payload.at(-2);
            ret.rxSlotIndex = payload.at(-1);
        }
        let data = payload.slice(DATA_POS, length);
        ret.port = port;
        ret.payload = data;
    }
    else {
        node.warn("length failure for data indication");
        ret = null;
    }
    return ret;
}

