var settings = flow.get("settings");
settings.confidence = parseInt(msg.payload);
flow.set("settings", settings);
return msg;

var settings = flow.get("settings");
if(!settings)
    return;

var list = [];

msg.payload.forEach( function(item){
    if (item.confidence < settings.confidence)
        return;
    if( item.age < settings.age)
        return;
    var dx = (item.cx - item.bx);
    dx = dx * dx;
    var dy = (item.cy - item.by);
    dy = dy * dy;
    item.distance = parseInt(Math.sqrt(dx+dy)/10);
    if( item.distance < settings.distance )
        return;

    var classMatch = false;
    settings.classes.forEach(function (element) {
        if (element === item.class)
            classMatch = true;
        if( item.class === "Face" && element === "Head")
            classMatch = true;
    });
    if (!classMatch)
        return;

    if( item.cx < settings.aoi.x1 )
        return;
    if (item.cx > settings.aoi.x2)
        return;
    if (item.cy < settings.aoi.y1)
        return;
    if (item.cy > settings.aoi.y2)
        return;
    list.push(item);
});


msg.payload = list;

return msg;

msg.payload = {
    service: "ObjectDetections",
    name: global.get("device").serial || "Undefined",
    aoi: {
        id: "aoi",
        x1: 200,
        y1: 200,
        x2: 800,
        y2: 800
    },
    age: 2,
    distance: 10,
    confidence: 30,
    classes: ["Human", "Car"],
    timeout: 5,
    threshold: 1
}

var device = global.get("device");
if( !device) 
    return msg;

if( device.hostname )
    msg.payload.name = device.hostname;

return msg;

if( msg.changes === null )
    return;

var device = global.get("device");
if(!device) {
    node.error("Missing global.device. Check Readme",msg);
    return;
}
var settings = flow.get("settings");
if(!settings) {
    node.error("Missing global.device.camera. Check Readme",msg);
    return;
}

var counter = flow.get("detectionsCounter") || 0;
if( msg.payload.length === counter )
    return;
counter = msg.payload.length;
flow.set("detectionsCounter",counter);
msg.topic = "presence/counter/" + device.serial;
msg.payload = counter;
return msg;

var threshold = flow.get("settings").threshold || 1;

if (msg.payload.length < threshold)
    return;

msg.delay = flow.get("settings").timeout * 1000;
return msg;

msg.topic = "init";
msg.payload = {
    aspect: "16:9",
    rotation: 0,
    resolution: "1280x720"
}

var camera = global.get("camera");
if ( camera ) {
    msg.payload.aspect = camera.aspect;
    msg.payload.rotation = camera.rotation;
    msg.payload.resolution = camera.largest;
}

return msg;
