var settings = flow.get("settings");
if(!settings)
    return;

if( msg.payload.distance < settings.distance) 
    return;

if( msg.payload.confidence < settings.confidence )
    return;

if( msg.payload.age < settings.age)  
    return;

var classMatch = false;
settings.classes.forEach(function(element) {
    if( element === msg.payload.class )
        classMatch = true;
});
if(!classMatch)
    return;

var aoiMatch = false;
msg.payload.path.forEach(function(position) {
    if (!aoiMatch && 
        position.x >= settings.aoi.x1 && 
        position.x <= settings.aoi.x2 &&
        position.y >= settings.aoi.y1 &&
        position.y <= settings.aoi.y2)
            aoiMatch = true;
});

if( !aoiMatch )
    return;

return msg;

msg.payload = {
    classes: ["Human", "Car"],
    age: 1,
    distance: 20,
    confidence: 30,
    aoi: {
        x1: 200,
        y1: 200,
        x2: 800,
        y2: 800
    },
}

return msg;

var settings = flow.get("settings");
settings.confidence = parseInt(msg.payload);
flow.set("settings", settings);
return msg;

msg.topic = "init";
msg.payload = {
    aspect: "16:9",
    rotation: 0,
    resolution: "1280x720"
}

var camera = global.get("camera");
if ( camera ) {
    msg.payload.aspect = camera.aspect;
    msg.payload.rotation = camera.rotation;
    msg.payload.resolution = camera.largest;
}

return msg;
