var settings = flow.get("settings");
if(!settings)
    return;

if( msg.payload.distance < settings.distance) 
    return;

if( msg.payload.confidence < settings.confidence )
    return;

if( msg.payload.age < settings.age)  
    return;

var classMatch = false;
settings.classes.forEach(function(element) {
    if( element === msg.payload.class )
        classMatch = true;
});
if(!classMatch)
    return;

var aoiMatch = false;
msg.payload.path.forEach(function(position) {
    if (!aoiMatch && 
        position.x >= settings.aoi.x1 && 
        position.x <= settings.aoi.x2 &&
        position.y >= settings.aoi.y1 &&
        position.y <= settings.aoi.y2)
            aoiMatch = true;
});

if( !aoiMatch )
    return;

return msg;

var device = global.get("device");
if(!device) {
    node.error("Missing global.device. Check Readme",msg);
    return;
}
if(!device.hasOwnProperty("camera")) {
    node.error("Missing global.device.camera. Check Readme",msg);
    return;
}

msg.topic = "image";
msg.camera = device.camera;
msg.resolution = device.camera.medium;

return msg;

var settings = flow.get("settings")
if(!settings)
    return;

msg.topic = "exception/" + settings.service + "/" + settings.name;
msg.payload = msg.error;
return msg;

var device = global.get("device");

if(!device) {
    node.error("Missing global.device. Check Readme",msg);
    return;
}

if(!device.hasOwnProperty("camera")) {
    node.error("Missing global.device.camera. Check Readme",msg);
    return;
}


msg.payload = {
    service: "ObjectPath",
    name: device.serial,
    aoi: {
        id: "aoi",
        x1: 200,
        y1: 200,
        x2: 800,
        y2: 800
    },
    age: 1,
    distance: 20,
    confidence: 30,
    classes: ["Human", "Car"]
}

if( device.hostname )
    msg.payload.name = device.hostname;

return msg;

var device = global.get("device");
if(!device)
    return;
var settings = flow.get("settings");
if(!settings)
    return;

msg.topic = "path/" + device.serial;
msg.payload.name = settings.name || device.name || device.serial;
msg.payload.serial = device.serial;

return msg;

var device = global.get("device");
if(!device)
    return;
var settings = flow.get("settings");
if(!settings)
    return;

msg.topic = "image/" + device.serial;
msg.payload = {
    name: settings.name || device.hostname || device.serial,
    serial: device.serial,
    service: settings.service,
    rotation: msg.camera.rotation,
    aspect: msg.camera.aspect,
    timestamp: new Date().getTime(),
    image: msg.payload,
}

return msg;

var settings = flow.get("settings");
settings.confidence = parseInt(msg.payload);
flow.set("settings", settings);
return msg;
