func:
var device = global.get("device");
if(!device)
    return;
var service = flow.get("service")
if(!service)
    return;
msg.topic = "exception/" + service.name + "/" + device.serial;
msg.payload = msg.error;
return msg;

var settings = flow.get("settings");
settings.confidence = parseInt(msg.payload);
flow.set("settings", settings);
return msg;

var settings = flow.get("settings");
if(!settings)
    return;

var trackers = flow.get("trackers") || {};
var counter = flow.get("counter") || 0;

msg.changes = null;

if( msg.payload.active === false ) {
    if( !trackers.hasOwnProperty(msg.payload.id) )
        return;
    delete trackers[msg.payload.id];
    counter--;
    if( counter <= 0 )
        counter = 0;
    flow.set("trackers",trackers);
    flow.set("counter",counter);
    msg.changes = counter;
    node.send(msg);
    return;
}

var ignore = false;

if( msg.payload.distance < settings.distance) 
    ignore = true;

if( !ignore && msg.payload.confidnece < settings.confidence )
    ignore = true;

if( msg.payload.age < settings.age)  
    ignore = true;

var classMatch = false;
settings.classes.forEach(function(element) {
    if( element === msg.payload.class )
        classMatch = true;
});
if(!classMatch)
    ignore = true;

if (msg.payload.cx < settings.aoi.x1 || 
    msg.payload.cx > settings.aoi.x2 ||
    msg.payload.cy < settings.aoi.y1 ||
    msg.payload.cy > settings.aoi.y2)
       ignore = true;

if( ignore  )
    return;

if( !trackers.hasOwnProperty(msg.payload.id)) {
    trackers[msg.payload.id] = msg.payload;
    counter++;
    flow.set("trackers",trackers);
    flow.set("counter",counter);
    msg.changes = counter;
}

var detections = 0;
for(var serial in trackers)
    detections++;

msg.presence = detections !== 0;

return msg;

var device = global.get("device");
if(!device)
    return;
if( !device.hasOwnProperty("camera") )
    return;

msg.topic = "image";

msg.payload = {
    camera: device.camera,
    image: msg.payload
}

return msg;

msg.payload = {
    service: "PresenceDetection",
    name: global.get("device").serial || "Undefined",
    aoi: {
        id: "aoi",
        x1: 200,
        y1: 200,
        x2: 800,
        y2: 800
    },
    age: 0,
    timeout: 5,
    distance: 0,
    confidence: 30,
    classes: ["Human", "Car"]
}

var device = global.get("device");
if( !device) 
    return msg;

if( device.hostname )
    msg.payload.name = device.hostname;

if( device.hasOwnProperty("camera") ) {
    msg.payload.aspect = device.camera.aspect;
    if(device.camera.rotation === 90 || device.camera.rotation === 270 )
    msg.payload.rotation = "Portrait";
}

return msg;

var aspect = flow.get("aspect") || "16:9";

switch( aspect ) {
    case "4:3":
        msg.resolution = "800x600"
        break;
    case "1:1":
        msg.resolution = "800x800"
        break;
    case "16:9":
    default:
        msg.resolution = "1280x720"
}

return msg;

if( msg.changes === null )
    return;

var device = global.get("device");
if(!device) {
    node.error("Missing global.device. Check Readme",msg);
    return;
}
var settings = flow.get("settings");
if(!settings) {
    node.error("Missing global.device.camera. Check Readme",msg);
    return;
}

msg.topic = "presence/counter/" + device.serial;
msg.payload = msg.changes;
return msg;

var device = global.get("device");
if(!device) {
    node.error("Missing global.device. Check Readme",msg);
    return;
}
var settings = flow.get("settings");
if(!settings) {
    node.error("Missing global.device.camera. Check Readme",msg);
    return;
}

msg.topic = "presence/state/" + device.serial;
msg.payload = msg.presence;
msg.delay = settings.timeout * 1000;
return msg;
