func:
msg.topic = "video";

if( msg.payload === false )
    return msg;

msg.payload.true;

switch(msg.payload) {
    case "2688x1512":
        msg.width = 2688,
        msg.height = 1512;
        break; 
    case "1920x1080":
        msg.width = 1920,
        msg.height = 1080;
        break; 
    case "1280x720":
        msg.width = 1280,
        msg.height = 720;
        break; 
    case "1024x576":
        msg.width = 1024,
        msg.height = 576;
        break; 
    case "640x360":
        msg.width = 640,
        msg.height = 360;
        break; 
    case "480x270":
        msg.width = 480,
        msg.height = 270;
        break; 
    case "320x180":
        msg.width = 320,
        msg.height = 180;
        break; 
    case "160x90":
        msg.width = 160,
        msg.height = 90;
        break; 
    default:
        msg.width = 800,
        msg.height = 450;
        break; 
}
return msg;
